import { Genre } from 'types/genre';
import { useEffect, useState } from 'react';
import { requestBackend } from 'util/requests';

import './styles.css';


type GenreFilterData = {
  genre: string;
};

type Props = {
    onSubmitFilter : (data : GenreFilterData) => void
}


const GenreFilter = ({onSubmitFilter} : Props) => {

  const [selectGenre, setSelectGenre] = useState<GenreFilterData>();

  const [genres, setGenre] = useState<Genre[]>([]);

  const onSubmit = (data : GenreFilterData) => {
    onSubmitFilter(data);
  }

  const handleChangeGenre = (value : GenreFilterData) => {
    const obj : GenreFilterData = {
        genre : getValues('genre')
    }
    onSubmitFilter(obj)
  }

  useEffect(() => {
    requestBackend({ url: '/genres', withCredentials: true }).then(
      (response) => {
        setGenre(response.data);
      }
    );
  }, []);

  return (
    <div className="genre-filter-container">
      <select name="genre" id="genre">
        {genres.map((genre) => (
          <option key={genre.id} value={genre.id}>
            {genre.name}
          </option>
        ))}
      </select>
    </div>
  );
};

export default GenreFilter;
