import { Link } from 'react-router-dom';
import GenreFilter from './GenreFilter';

import './styles.css';
import { useEffect, useState } from 'react';
import { Genre } from 'type/genre';
import { requestBackend } from 'util/requests';
import { Movie } from 'type/movie';
import MovieCard from './MovieCard';

const MovieCatalog = () => {
  const [movies, setMovie] = useState<Movie[]>([]);

  const [genres, setGenre] = useState<Genre[]>([]);

  useEffect(() => {
    requestBackend({ url: '/genres', withCredentials: true }).then(
      (response) => {
        setGenre(response.data);
      }
    );
  }, []);

  useEffect(() => {
    requestBackend({ url: '/movies', withCredentials: true, params: {page: 0, size:12} }).then(
      (response) => {
        const movie = response.data as Movie;
        setMovie(movies);
      }
    );
  }, []);

  return (
    <>
      <div className="movie-catalog-container">
        <div className="movie-catalog-content">
          <h1>Tela de listagem de filmes</h1>
          <GenreFilter genre={genres} />

            {movies.map((movie) => (<MovieCard movie={movie} />))}

          <Link to="/movies/1">
            Acessar /movies/1<br></br>
          </Link>
          <Link to="/movies/2">Acessar /movies/2</Link>
        </div>
      </div>
    </>
  );
};

export default MovieCatalog;
